reset && set -e && echo -e "getting logs...\n" && timestamp=$(date +"%Y-%m-%d_%H-%M-%S_%Z") && sudo -E docker logs -t nosana-node >> ~/logs/nosana-node_$timestamp.UTC-timestamps.log && sudo cp ~/logs/nosana-node.log ~/logs/nosana-node_$timestamp.original-backup.log && sudo chown $(whoami):$(whoami) ~/logs/nosana-node_$timestamp.original-backup.log && ls -t -r -l ~/logs/*$timestamp* || echo "No matching log files found." && echo -e "\nALL BACKUP LOGS ARE LOCATED IN ~/logs        cd ~/logs\n" && echo -e "\n   View the etnire log with:      cat ~/logs/nosana-node_$timestamp.UTC-timestamps.log ; echo\nOr view the end of the log with:  tail -n 2000 ~/logs/nosana-node_$timestamp.UTC-timestamps.log; echo\n"
